mapper.select:
SELECT A.POST_CODE AS "code" , B.AUTH_STATUS AS "authStatus" , A.POST_TITLE AS "title" , C.USER_ID AS "sellerId" , A.BLIND_YN AS "blindYn" , A.POST_TIME AS "time" FROM POST A JOIN AUTHENTIC B ON (A.AUTH_CODE = B.AUTH_CODE) JOIN "USER" C ON (A.SELLER_CODE = C.USER_CODE)
A.AUTH_CODE = #{authCode}
AND A.BLIND_YN = #{blindYn}
AND C.USER_ID = #{sellerId}
AND A.POST_TITLE LIKE '%' || #{title} || '%'
AND A.POST_CONTENT LIKE '%' || #{content} || '%'
ORDER BY A.POST_CODE DESC

mapper.insert:
		INSERT
		  INTO Post A
		(
		  A.POST_CODE
        , A.SELLER_CODE
        , A.POST_CAT_CODE
        , A.POST_TITLE
        , A.POST_CONTENT
        , A.ITEM_PRICE
		)
		VALUES
		(
		  SEQ_POST_CODE.NEXTVAL
		, #{sellerCode}
		, 1
		, #{title}
		, #{content}
		, #{itemPrice}
		)

mapper.update:
UPDATE
	   POST A
   SET A.BLIND_YN = #{blindYn}
 WHERE A.POST_CODE = #{code}

UPDATE
	   POST A


		A.POST_TITLE = #{title}


		A.POST_CONTENT = #{content}


		A.ITEM_PRICE = #{itemPrice}



 WHERE A.POST_CODE = #{code}

mapper.delete:
DELETE
  FROM POST A
 WHERE A.POST_CODE = #{code}
