mapper.insert:
INSERT
  INTO "USER" A
(
  A.USER_CODE
, A.USER_ID
, A.USER_PSW
, A.USER_EMAIL
, A.PHONE
, A.USER_NAME
, A.BIRTH_DATE
, A.GENDER
)
  VALUES
(
  SEQ_USER_CODE.NEXTVAL
, #{ id }
, #{ psw }
, #{ email }
, #{ phone }
, #{ name }
, #{ date }
, #{ gender }
)

mapper.update:
UPDATE
       "USER" A
   SET A.USER_EMAIL = #{ email }
     , A.PHONE = #{ phone }
     , A.USER_NAME = #{ name }
     , A.BIRTH_DATE = #{ date }
     , A.GENDER = #{ gender }
     , A.DEPOSIT = #{ deposit }
 WHERE A.USER_CODE = #{ code }

UPDATE
       "USER" A
   SET A.ENT_CODE = #{ entCode }
 WHERE A.USER_CODE = #{ code }
